import { Component, OnInit } from '@angular/core';
import { POKEMONS } from './mock-pokemon-list';
import { Pokemon } from './pokemon';


@Component({
  selector: 'app-root',
  templateUrl:'app.component.html'
  })
export class AppComponent implements OnInit{
  pokemonList : Pokemon[] = POKEMONS;
  pokemonSelected : Pokemon|undefined;

  ngOnInit() {
    console.table(this.pokemonList);
  }

  selectPokemon(pokemonId : string){
    const id = +pokemonId;
    const pokemon : Pokemon|undefined = this.pokemonList.find(pokemon => pokemon.id == id);
    if(pokemon){
      console.log(`vous avez demandé le pokemon ${pokemon.name}`);
      this.pokemonSelected = pokemon;
    }else{
      console.log(`vous demander un pokemon qui n'existe pas`);
      this.pokemonSelected = pokemon;
    }

  }
}
<h1>
    Liste de Pokémons
</h1>
<input 
    #input
    (keyup.enter)="selectPokemon(input.value)"
    type="number" 
>

<p *ngIf="pokemonSelected">
    Vous avez sélectionné le pokemon : <strong>{{ pokemonSelected?.name }}</strong>
</p>

<p>{{pokemonList[0].name}}</p>
<p> {{pokemonList[1].name}}</p>
<p> {{pokemonList[2].name}}</p>
<p> {{pokemonList[3].name}}</p>
