import { Component, OnInit } from '@angular/core';
import { POKEMONS } from './mock-pokemon-list';
import { Pokemon } from './pokemon';


@Component({
  selector: 'app-root',
  templateUrl:'app.component.html'
  })
export class AppComponent implements OnInit{
  pokemonList : Pokemon[] = POKEMONS;
  pokemonSelected : Pokemon|undefined;

  ngOnInit() {
    console.table(this.pokemonList);
  }

  selectPokemon(pokemonId : string){
    const id = +pokemonId;
    const pokemon : Pokemon|undefined = this.pokemonList.find(pokemon => pokemon.id == id);
    if(pokemon){
      console.log(`vous avez demandé le pokemon ${pokemon.name}`);
      this.pokemonSelected = pokemon;
    }else{
      console.log(`vous demander un pokemon qui n'existe pas`);
      this.pokemonSelected = pokemon;
    }

  }
}
<h1 class="center">
    Liste de Pokémons
</h1>

<div class="container">
    <div class="row">
        <div *ngFor="let pokemon of pokemonList" class="col m4 s6">
            <div class="card horizontal"  pkmnBorderCard>
                <div class="card-image">
                    <img [src]="pokemon.picture" alt="">
                </div>
                <div class="card-stacked">
                    <div class="card-content">
                        <p>{{pokemon.name}}</p>
                        <p><small>{{pokemon.created | date:"dd/MM/yyyy"}}</small></p>
                        <span *ngFor="let type of pokemon.types" class="{{type | pokemonTypeColor}}">
                            {{type}}
                        </span>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
